/*
-- NPC Bots have creature id >= 70000
-- These queries will update their levels for 255 gameplay and populate the world (alliance only) with bots and bot givers.
*/
update creature_template set minlevel = minlevel+170 where entry >=70000 and minlevel >=80;
update creature_template set maxlevel = maxlevel+170 where entry >=70000 and maxlevel >=80;
DELETE FROM `creature` WHERE `id` >=70000;
